<style>
  #member {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    box-sizing: border-box;
    padding: 12px;
    background-color: var(--theme-day-bg);
    z-index: 100;
  }

  #member a {
    display: block;
    width: 32px;
    height: 32px;
    text-decoration: none;
  }

  .alert_icon {
    overflow: hidden;
    position: relative;
    color: var(--text-white);
  }

  .alert_icon>#alert_count {
    display: block;
    width: 15px;
    font-size: 10px;
    text-align: center;
    position: absolute;
    top: 2px;
    right: 2px;
    z-index: 10000;
    font-weight: 700;
    color: var(--theme-day-bg);
  }


  .right_wrap {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 0.5rem;
  }
</style>

