<style>
  header.sub {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    height: 56px;
    background-color: var(--theme-day-bg);
    box-sizing: border-box;
    padding: 15px 12px 15px 14px;
    /* position: sticky;
    top: 0px;
    left: 0px;
    z-index: 1000; */
    margin-top: 0px;
  }

  header.sub.none {
    display: none;
  }

  header.sub a {
    display: flex;
    align-items: center;
    gap:0.25rem;
  }

  header.sub img,
  header.sub h1 {
    display: block;
  }

  header.sub a h1 {
    box-sizing: border-box;
    padding-top: 3px;
    margin-bottom:0px;
  }

  header.sub a h1 img {
    height: 100%;
  }

  header.sub ul {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 8px;
    list-style: none;
    margin: 0px;
  }


  header.sub ul>li.gift {
    position: relative;
  }

  header.sub ul>li.gift a #alert_count {
    display: none;
    width: 15px;
    font-size: 10px;
    text-align: center;
    position: absolute;
    top: 2px;
    right: 2px;
    z-index: 10000;
    font-weight: 700;
    color: var(--theme-day-bg);
  }

  header.sub ul>li.gift a svg>circle {
    stroke: none;
  }

  header.sub ul>li.gift a #alert_count.active {
    display: block;
  }


  /* 성인작 노출 토글 추가 - 24.01.04 */
  
  header.sub ul>li label[for="adult"] .toggle-wrap{
    display:block;
    width: 54px;
    height:24px;
    border-radius: 999px;
    position: relative;
    background-color: var(--border-e0);
    padding: 3px;
  }
  header.sub ul>li label[for="adult"] .toggle-wrap.active{
    background-color: var(--warning);
  }
  header.sub ul>li label[for="adult"] .toggle-wrap .toggle-switch{
    display:block;
    width:18px;
    height:18px;
    border-radius:999px;
    position:absolute;
    top:3px;
    margin-left:0px;
    margin-right:auto; 
    background-color:var(--theme-day-bg);
    font-size:10px;
    line-height:18px;
    font-weight:800;
    text-align:center;
    color:var(--text-primary);
    cursor: pointer;
    transition-duration:0.15s;
    transition-timing-function:ease-in-out;
  }
  header.sub ul>li label[for="adult"] .toggle-wrap.active .toggle-switch{
    margin-left:54%;
    margin-right:0px;
    color:var(--text-333);
  }

  header.sub ul>li label[for="adult"] .toggle-wrap .toggle-notice{
    display:block;
    font-size:10px;
    line-height:1.7;
    font-weight:800;
    color: #FBFBFB;
    text-transform:uppercase;
  }
  header.sub ul>li label[for="adult"] .toggle-wrap.active .toggle-notice:first-of-type{
    display:block;
    padding-left: 7px;
  }
  header.sub ul>li label[for="adult"] .toggle-wrap .toggle-notice:last-of-type{
    text-align:right;
    padding-right: 7px;
  }
  header.sub ul>li label[for="adult"] .toggle-wrap .toggle-notice:first-of-type,
  header.sub ul>li label[for="adult"] .toggle-wrap.active .toggle-notice:last-of-type{
    display:none;
  }
</style>


