<script>
  $(document).ready(function () {
    let path = window.location.pathname;

    function subRandomLogo(pathName) {
      // comic,novel,webtoon 으로 시작하는 이미지 각각 35개
      let randomLogoNum = Math.floor(Math.random() * 36);
      let randomLogo = document.querySelector('.random-logo');
      randomLogo.innerHTML = '<img src="/assets/images/kr/random_logo/' + pathName + randomLogoNum + '.svg" alt="꿀툰">';
    }

    if (path.includes('webtoon')) {
      subRandomLogo('webtoon')
    } else if (path.includes('comic')) {
      subRandomLogo('comic')
    } else if (path.includes('novel')) {
      subRandomLogo('novel')
    }

      /* 선물함 페이지 이동 url 세팅*/
      $(".gift").click(function () {

          // 회원 정보 조회
          let memberInfo = local.memberInfo();

          // 비로그인
          if (memberInfo == null) {
              // 로그인 모달
              toast.alert("로그인 후 이용해주세요");
              movePage.login();

              // 로그인
          } else {
              // 선물함 페이지 이동
              movePage.gift();
          }
      });

      /* 선물함 지급 개수 세팅 */
      let memberInfo = local.memberInfo(); // 회원 정보 조회
      if (memberInfo != null) { // 로그인

          // 쿠키 조회
          let giftCnt = $.cookie('giftCnt');

          // 쿠키 있으면
          if (giftCnt != undefined) {

              if (parseInt(giftCnt) > 0) {
                  $(".gift").find("circle").show();
                  $("#alert_count").html(giftCnt);

              } else {
                  $("#alert_count").removeClass("active");
              }

              // 쿠키 없으면
          } else {
              $(".gift").find("circle").show();
              $("#alert_count").html(5);
          }
      } else { // 비로그인
          $(".gift").find("circle").show();
          $("#alert_count").html(5);
      }

      /* 로컬 스토리지 시청연령 토글 선택값 조회 */
      let pavilion = local.pavilion();
      if (pavilion != null && pavilion.state.pavilionIdx == 1) {
          // 토글 ON
          $(".sub #header_nav li:first").find(".toggle-wrap").addClass("active");
      } else {
          // 토글 OFF
          $(".sub #header_nav li:first").find(".toggle-wrap").removeClass("active");
      }
  });
</script>